function totalQtFonteLuz(n){for(var t=0,u=0,a=1;a<n.length;a++)u=new Number($(n[a]).val()),t+=u;return t}function totalPotenciaFonteLuz(n){for(var t=0,u=0,a=0,e=0,c=0,i=0,r=1;r<n.length;r++)e=$(n[r]).parent("td").parent("tr").find("td").eq(2).find("input").val(),a=$(n[r]).parent("td").parent("tr").find("td").eq(3).find("select").val(),a==OUTROS?(i=$(n[r]).parent("td").parent("tr").find("td").eq(5).find("input").val(),t=new Number(e)*new Number(i)):(u=$(n[r]).val(),t=""==u?0:new Number(e)*new Number(tipoFonteLuz[a].potencia[u])),c+=t;return c}function totalArea(n){for(var t=0,u=0,a=1;a<n.length;a++)u=new Number($(n[a]).val()),t+=u;return t}function calculaPotenciaInicial(n,t,u){return n*t*(1+u)}function calculaConsumoInicial(n,t){return n*t/1e3}function calculaCustoEnergiaInicial(n,t){return n*t}function calculaFluxoLuminosoInicial(n,t,u){return n*t*u}function calculaIluminanciaInicial(n,t){return n/t}function calclulaDpiInicial(n,t,u){return n/t/u*100}function calculaPotenciaFinal(n,t){return n/t}function calculaFonteLuzFinal(n,t,u){return n-t*u/1e3}function calculaMedidasSimNaoFinal(n,t,u){return n*t*u/1e3}function calculaConsumoFinal(n,t,u,a,e){return n-t-u-a-e}function calculaCustoEnergiaFinal(n,t){return n*t}function calculaReducaoAnualFinal(n,t){return(n-t)/n}function calculaMedidasInvestimento(n,t){return n*t/1e3}function calculaInvestimentoResultadoInvestimento(n,t,u,a){return n+t+u+a}function calculaReducaoCustos1Investimento(n,t){return n-t}function calculaReducaoCustos2Investimento(n,t){return n/t}function calculaPriInvestimento(n,t){return n/t}